"use strict";

var inputs = document.querySelectorAll(".vlogers__select-input"),
    options = document.querySelectorAll(".vlogers__option"),
    optionsItem = document.querySelectorAll(".vlogers__option-item");
var open = !1;
document.body.addEventListener("click", function (t) {
  "open" === t.target.dataset.open || t.target.classList.contains("disabled") ? t.target.classList.contains("disabled") ? t.target.parentNode.parentNode.classList.add("_active") : (open = !open, document.querySelectorAll('[data-dropdown="dropdown"]').forEach(function (e) {
    e.classList.remove("_active"), e.dataset.target === t.target.id && open ? e.classList.add("_active") : e.classList.remove("_active");
  })) : document.querySelectorAll('[data-dropdown="dropdown"]').forEach(function (t) {
    t.classList.remove("_active");
  });
}), options.forEach(function (t) {
  t.addEventListener("click", function (e) {
    inputs.forEach(function (a) {
      e.target.classList.contains("disabled") ? t.classList.add("_active") : a.id === t.dataset.target && (a.value = e.target.innerHTML, a.dataset.value = e.target.dataset.value, open = !1);
    });
  });
});