"use strict";

var inputs = document.querySelectorAll(".vlogers__select-input"),
    options = document.querySelectorAll(".vlogers__option"),
    optionsItem = document.querySelectorAll(".vlogers__option-item");
var open = !1;
document.body.addEventListener("click", function (e) {
  "open" === e.target.dataset.open || e.target.classList.contains("disabled") ? e.target.classList.contains("disabled") ? e.target.parentNode.parentNode.classList.add("_active") : (open = !open, document.querySelectorAll('[data-dropdown="dropdown"]').forEach(function (t) {
    t.classList.remove("_active"), t.dataset.target === e.target.id && open ? t.classList.add("_active") : t.classList.remove("_active");
  })) : document.querySelectorAll('[data-dropdown="dropdown"]').forEach(function (e) {
    e.classList.remove("_active");
  });
}), options.forEach(function (e) {
  e.addEventListener("click", function (t) {
    inputs.forEach(function (a) {
      t.target.classList.contains("disabled") ? e.classList.add("_active") : a.id === e.dataset.target && (a.value = t.target.innerHTML, a.dataset.value = t.target.dataset.value, open = !1);
    });
  });
});
var headerTop = document.querySelector(".header__top"),
    nav = document.querySelector(".header__top-nav"),
    languages = document.querySelector(".header__top-lang"),
    burger = document.querySelector(".header__burger");
window.addEventListener("resize", function (e) {
  addChild(window.innerWidth);
});
var windowInnerWidth = window.innerWidth;

function addChild(e) {
  e <= 768 ? nav.appendChild(languages) : headerTop.appendChild(languages);
}

addChild(windowInnerWidth), burger && burger.addEventListener("click", function () {
  burger.classList.toggle("_openMenu"), burger.classList.contains("_openMenu") ? (nav.classList.add("_open"), document.body.classList.add("_lock")) : (nav.classList.remove("_open"), document.body.classList.remove("_lock"));
});